@app.route('/search', methods=['GET'])
def signin():
	#get input information
	strs=request.args.get('query')
	Text=""
	#preread the css etc from Select.txt
	with open("Select.txt", "r", encoding='utf-8') as fw:
		Text = fw.read()
		
	with open("input.txt", "w", encoding='utf-8') as f:
		f.write(strs);

	word_list = []
	T = ""
	filename = "input.txt"
	#use jieba to cut query into "input.txt"
	with open(filename, "r") as f:
		words = pseg.cut(f.read(),use_paddle=False) #paddle mode
		for word, flag in words:
			if flag == 'x':
				continue
			if flag == "eng":
				continue
			T = T + '[' + word + ' ' + flag + ']'
			word_list.append(word)
        #write result after cut
	with open(filename, "w") as fw:
		fw.write(T)
	#run the C++ program to init inverse retrieval
	os.system("./Init")
	#read result
	with open("result.txt", "r", encoding='utf-8') as fw:
		strs=fw.read()
        #find corresponding url
	#get them into html form to look better
	pos = 0
	while pos != -1:
		St = pos
		pos = strs.find('\n', St)
		if pos == -1:
			break
		now = strs[St:pos]
		now_url = "http://" + doc_dict[now]
		Text = Text + '<p><a class="subtitle" href="' + now_url + '" target="_blank">'
		filename = now + "ext.txt"
		tmp = ""
		#find title information as the search result
		with open(filename, "r", encoding="utf-8") as f:
			tmp = f.read()

		Left = tmp.find('>', 0)
		Right = 0
		if Left != -1:
			Right = tmp.find('<',Left)
			Text = Text + tmp[(Left+1):Right]
		Text = Text + '</a></p>' + '\n'
		
		#get the abstract part about 100 words to return
		First = Right
		Last = Right
		Cnt = 0
		Val = 100
		for i in word_list:
			stpos = tmp.find(i, Last)
			if stpos == -1:
				continue
			Cnt = Cnt + 1
			if Cnt == 1:
				First = stpos-5
				Text = Text + '<p class="result">' + tmp[First:stpos]
			nowpos = tmp.find(i, Last)
			if Last != Right:
				if Last < nowpos:
					if (nowpos - First) >= Val:
						Text = Text + tmp[Last:(First+Val)]
						break
					else:
						Text = Text + tmp[Last:nowpos]
				
			Text = Text + '<strong>' + i + '</strong>'
			Last = nowpos + len(i)
			if (Last - First) >= Val:
				break
			
		if Last - First < Val:
			Text = Text + tmp[Last:(First+Val)]
		Text = Text + '</p>' + '\n'	
		pos = pos + 1
	
	print(Text)
	Text = Text + '</div>' + '\n' + '</body>' + '\n' + '</html>' + '\n'
	return Text

#preinit every docid's url
def get_url():
	pos = 1
	Text = ""
	St = 1
	Right = 0
	with open("url_list.txt", "r") as f:
		Text = f.read()
		while pos <= 6883:
			Left = Text.find(':', Right)
			if Left == -1:
				break
			Right = Text.find('\n', Left)
			doc_dict.update({str(pos) : Text[Left+1:Right]})
			pos = pos + 1
			Right = Right + 1
			
if __name__ == '__main__':
	get_url()
	app.run()
